    private void bResultActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bResultActionPerformed
        int n1, n2, res;
        float num, resf;

        try {

            if (!cResult.getText().equals("")) {
                number2 = cResult.getText();
            }

            switch (operacion) {
                case '+':
                    n1 = Integer.parseInt(number1);
                    n2 = Integer.parseInt(number2);

                    res = n1 + n2;

                    result = String.valueOf(res);

                    cResult.setText(result);
                    break;
                case '-':
                    n1 = Integer.parseInt(number1);
                    n2 = Integer.parseInt(number2);

                    res = n1 - n2;

                    result = String.valueOf(res);

                    cResult.setText(result);
                    break;
                case '*':
                    n1 = Integer.parseInt(number1);
                    n2 = Integer.parseInt(number2);

                    res = n1 * n2;

                    result = String.valueOf(res);

                    cResult.setText(result);
                    break;
                case '/':
                    n1 = Integer.parseInt(number1);
                    n2 = Integer.parseInt(number2);

                    if (n2 != 0) {
                        res = n1 / n2;

                        result = String.valueOf(res);

                        cResult.setText(result);
                    } else {
                        cResult.setText("No se puede dividir por cero");
                    }
                    break;
                default:
                    throw new AssertionError();
            }
        } catch (ArithmeticException ex) {
            cResult.setText("No se puede por cero");
        } catch (NumberFormatException ex) {
            cResult.setText("Introduce un numero primero");
        } catch (NullPointerException ex) {
            cResult.setText("Introduce un segundo numero primero");
        }
    }//GEN-LAST:event_bResultActionPerformed

