    private void bComaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bComaActionPerformed
        String cad;

        cad = cResult.getText();

        if (cad.length() <= 0) {
            cResult.setText("0.");
        } else {
            if (!existepunto(cResult.getText())) {
                cResult.setText(cResult.getText() + ".");
            }
        }
    }//GEN-LAST:event_bComaActionPerformed

    private void bRetroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bRetroActionPerformed
        String cad;
        cad = cResult.getText();

        if (cad.length() > 0) {
            cad = cad.substring(0, cad.length() - 1);
            cResult.setText(cad);
        }

        cResult.setText(cad);
    }//GEN-LAST:event_bRetroActionPerformed

