    private void bBi_DeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bBi_DeActionPerformed
        int n_binario;
        int n_decimal;
        boolean esBinario;

        if (!cResult.getText().equals("")) {

            try {
                number1 = cResult.getText();
                cResult.setText("");
                esBinario = esbinario(number1);
                if (esBinario) {
                    number1 = convertirADecimal();
                    cResult.setText(number1);
                }
            } catch (Exception e) {
                if (!esbinario(number1)) {
                    javax.swing.JOptionPane.showInternalMessageDialog(null, "No es un número binario", "Error", javax.swing.JOptionPane.INFORMATION_MESSAGE);
                }

            }
        } else {
            cResult.setText("No hay nada en la caja");
        }
    }//GEN-LAST:event_bBi_DeActionPerformed

