    private String convertirADecimal() {
        int n1;
        int dec = 0;
        int ult = 0;
        int exp = 0;
        int n2;
        String n;
        n1 = Integer.parseInt(number1);

        n2 = n1;

        while (n2 != 0) {
            ult = n2 % 10;
            n2 = n2 / 10;
            System.out.println(n2);
            dec = dec + ult * (int) Math.pow(2, exp);
            exp++;
        }
        n = String.valueOf(dec);

        return n;
    }

